function pageprocessorSignIn279(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  try {
    $rootScope.back = function() {
      if ($.jStorage.get('tenantInfo').guestApp) {
        $location.path("/app/Menu82/Menu82");
