    //  $(".backButtonArrow").hide();
    //}

    $scope.termsServices = pagedef.termServices;
    $scope.policy = pagedef.policy;
    $scope.forgotPass = pagedef.forgotPass;

    $scope.username = '';
    $scope.password = '';
    var authSuccess = function(data) {
      if (!data.access_token) {
        $.unblockUI();
        if (window.device) {
          navigator.notification.alert(
            'Unable to login, please try again', // message
            null, // callback
            'Error', // title
            'OK' // buttonName
          );
        } else {
          alert('Unable to login, please try again')
        }
        return;
      }


      if (data.status && data.status == 500) {
        $.unblockUI();
        if (window.device) {
          navigator.notification.alert(
            'Invalid credentials', // message
            null, // callback
            'Error', // title
            'OK' // buttonName
          );
        } else {
          alert('Invalid credentials')
        }
        return;
      }

      $.jStorage.set("unifyedusername", $scope.username + '@' + $rootScope.user.tenantdomain);
      $.jStorage.set("unifyedpassword", $scope.password);

      $rootScope.user['accessToken'] = data.access_token;
      $rootScope.user['refreshToken'] = data.refresh_token;
      $rootScope.user['providerData'] = data.access_token;
      $rootScope.erpticket = {};
      $rootScope.erpticket[$.jStorage.get('tenantInfo').erp.name] = data.access_token;
      $rootScope.loggedIn = true;
      if ($.jStorage.get("homeScreen") == null || $.jStorage.get("homeScreen") == undefined) {
        $.jStorage.set("homeScreen", "whatsup");
        $rootScope.homeScreen = "whatsup";
      } else {
        $rootScope.homeScreen = $.jStorage.get("homeScreen");
      }

      // Draw dock icons
      angular.forEach($rootScope.appgroups, function(value, key) {
        angular.forEach(value.apps, function(val1, key1) {
          if (val1.displayname == "WhatsUp") {
            val1.showInDock = ($rootScope.homeScreen == "whatsup") ? true : false;
          } else if (val1.displayname == "QuickLaunch") {
            val1.showInDock = val1.showInDock = ($rootScope.homeScreen == "ql") ? true : false;
          }
        });
      });
      // Redraw the dock icons
