        '<img style="width:20px;" src="{{app.logo}}" alt="">' +
        '</a></div>' +
        '<div class="clearfix"></div>'
      '</div>';
      //var htmlView = $compile($(data2))($scope);

      //$("#footerContentContainer").html(htmlView);

      var options = {
        method: 'GET',
        url: $rootScope.user.oauthUserInfoUrl,
        qs: {
          schema: 'openid'
        },
        headers: {
          Authorization: 'Bearer ' + $rootScope.user.accessToken
        }
      };

      $rootScope.callOpenAPI(options, function(err, userInfo) {

        if (err) {
          $.unblockUI();
          if (window.device) {
            navigator.notification.alert(
              'Unable to login, please try again', // message
