        } else {
          div = document.getElementById('postTitle');
          div.insertAdjacentHTML('beforeend', "<div></div>");
        }
      }
    }
  };

  function _toConsumableArray(arr) {
    return _arrayWithoutHoles(arr) || _iterableToArray(arr) || _nonIterableSpread();
  }

  function _nonIterableSpread() {
    throw new TypeError("Invalid attempt to spread non-iterable instance");
  }

  function _iterableToArray(iter) {
    if (Symbol.iterator in Object(iter) || Object.prototype.toString.call(iter) === "[object Arguments]") return Array.from(iter);
  }

  function _arrayWithoutHoles(arr) {
    if (Array.isArray(arr)) {
      for (var i = 0, arr2 = new Array(arr.length); i < arr.length; i++) {
        arr2[i] = arr[i];
      }
      return arr2;
    }
  }


  $scope.fetchPreviewData = function(linksArray) {
    var data = {};
    data.links = linksArray;
    $.ajax({
      url: "/getLinkPreview",
      method: "POST",
      data: data,
      success: function success(data) {
        $scope.previewData = [].concat(_toConsumableArray(data), _toConsumableArray($scope.previewData));

        if (data.length > 0) {
          $scope.urlsForPreview(data);
        }
      },
      error: function error(e) {
        $.unblockUI();
      }
    });
  };

  $scope.postTitleLength="";
  $scope.onKeyUp = function(event) {
    $scope.postTitleLength = document.getElementById('postTitle').innerText.trim().length;

    if ($scope.postTitleLength < 3) {
