        /*  var index = $scope.posts.findIndex(function(x) {
         return x.id == id
         });*/
        var index = -1;
        $scope.posts.some(function(el, i) {
          if (el.id == id) {
            index = i;
            return true;
          }
        });

        try {
          $rootScope.isblocking = true;
          var feedUrl = "/unifydwhatsup/comment";
          var data = {
