              $scope.posts[index].comment += 1;
              $scope.posts[index].comments.push(data.data);

              if (!$scope.posts[index].showViewAllComment) {
                $scope.posts[index].limitComment = $scope.posts[index].comment;
              }

              $scope.$apply();
            }
          }); //console.log($scope.posts);
        } catch (e) {
          alert(e);
        }

        $("#commentBox" + id).val("");
        $("#commentBox" + id).closest(".media-body").find(".replybtn").removeClass('themeText').addClass('replybtnText'); //$(this).closest(".media-body").find(".addCommentInput").val("");
        //$("#commentBox"+id).closest('replybtn').removeClass('yellowText').addClass('replybtnText');
      }
    } catch (e) {
      alert(e);
    }
  };

  $window.onclick = function(event) {
