    if ($scope.showCreatePost) {
      if ($scope.postImage.length == 0 && document.getElementById('postTitle').innerText.trim().length == '' && !$scope.youtubeContainer && $scope.postTags.length == 0){
        $scope.showCreatePost = false;
        $scope.$apply();
