  };

  $scope.trusthtml = function(data) {
    return $sce.trustAsHtml(decodeURIComponent(data));
  };

  $scope.loadYTFrame = function($event) {
    var elem = $event.currentTarget;
    var newEle = angular.element("<iframe></iframe>");
    var embed = "https://www.youtube.com/embed/ID";
    newEle.attr("src", embed.replace("ID", angular.element($event.currentTarget).attr('data-id')));
    var e = $compile($(newEle))($scope);
    $(elem).replaceWith($(e));
  };

  setTimeout(function() {
    $('.dropdown').keyup(function(e) {
      if (e.keyCode == 40) {
        $(this).addClass('open');
      } else if (e.keyCode == 38) {
        $(this).removeClass('open');
      }
    });
    $(".postDesc a").on('click', function(e) {
      console.log(1);
      var url = $(this).attr('href');
      var target = $(this).attr('target');

      if (target == '_blank') {
        e.preventDefault();
        e.stopPropagation();
        var iabRef;

        if (window.device) {
          if (device.platform == 'iOS') {
            $.blockUI();
            iabRef = window.open(url, "_blank", "location=yes,hidden=yes,enableViewportScale=yes,clearcache=yes,closebuttoncaption=close");
          } else {
            window.open(url, "_system");
          }

        } else {
          iabRef = window.open(url, "_blank"); //window.history.back();
        }

        var hideBlockUi = function hideBlockUi() {
          $.unblockUI();
          iabRef.show();
        };

        var iabClose = function iabClose(data) {
          $.unblockUI();
          window.StatusBar.backgroundColorByHexString($rootScope.statusBarColor ? $rootScope.statusBarColor : '#000000');
          iabRef.removeEventListener("exit", iabClose);
          window.screen.orientation.lock('portrait'); //window.history.back();
        };

        iabRef.addEventListener("exit", iabClose);

        var loadStop = function loadStop(data) {
          hideBlockUi();
          iabRef.removeEventListener("loadstop", loadStop);
        };

        iabRef.addEventListener("loadstop", loadStop); //window.open(url, '_blank');
      }
    });
  }, 1000);
}

