//Resize and Crop Starts(Robin) 

 
$scope.bindDrag = function(){
  
     var container = $(".ModalcropBody");
     
     $("#canvas").resizable({
    aspectRatio:true,
    handles: "n, e, s, w",
    minWidth:115,
    minHeight:115,
    maxWidth:$scope.pixelsWid,
    maxHeight:$scope.pixelsHeight,
    containment: container
    }).draggable({
    containment: container
    });
    
}
  
    
  var image = new Image();

  $scope.addCropModal = function() {
  
    $("#profileCropModal").modal("show");
  
  $(".modal-backdrop").css("display","none");
  
  var getModalWidth = $("#profileCropModal .cropModalContent").width();

    image.src = $scope.targetImage;

    var modalCanvas = document.getElementById("modalCanvas");
    var ctx = modalCanvas.getContext("2d");
  
  $scope.decreaseValue = $scope.ReaderimgWid - getModalWidth;
    $scope.getPerDecresceVal =  ($scope.decreaseValue / $scope.ReaderimgWid) * 100;
  
  $scope.pixelsWid =  $scope.ReaderimgWid - $scope.ReaderimgWid*($scope.getPerDecresceVal/100);
  $scope.pixelsHeight =  $scope.ReaderimgHeight - $scope.ReaderimgHeight*($scope.getPerDecresceVal/100);
    
    modalCanvas.width = $scope.pixelsWid - 12;
  modalCanvas.height = $scope.pixelsHeight;

    image.onload = function(e) {
      ctx.drawImage(image,
        0, 0, image.width, image.height,
        0, 0, modalCanvas.width, modalCanvas.height
      );

      var createModalImage = new Image();
      createModalImage.src = modalCanvas.toDataURL();

      $scope.ModalCropImage = createModalImage.src;
    
    $('#canvas').css('width', 120).css('height', 120);
    
    $scope.getBase64 = "";
    $scope.canvas = document.getElementById('Canvasss');
    $scope.context = $scope.canvas.getContext('2d');
    $scope.imageObj =  new Image();
    
    $scope.bindDrag();
    $scope.$applyAsync();
    
    };
}
    
  $scope.profileCrop = function() {

   $scope.imageObj.src = $(".ModalCropImage").attr("src");

      $scope.imgWidth = $("#canvas").width();
      $scope.imgHeight = $("#canvas").height();
      $scope.imgPos = $("#canvas").position();
    
    $scope.sourceX = $scope.imgPos.left;
      $scope.sourceY = $scope.imgPos.top;

      $scope.sourceWidth = $scope.imgWidth;
