  $("#appContent").append(data1);
  $rootScope.isblocking = false;
  var rootUrl = $rootScope.GatewayUrl;
  var counter = 0;


  var proxyMethod = "GET";
  var proxyData = {};
  $scope.qlTenantid = "";
  $scope.QLSSOApps = [];
  $.blockUI();
  $rootScope.isblocking = true


  $scope.qlAppSelect = function(app) {
    var appname = app.description;
    if (appname) {
      $rootScope.logPageAccess($rootScope.tenant, appname, appname, appname, function() {
        // Tracking info sent to analytics
      });
    }
  }

  function initApp() {
    var ssoEndpoint = $rootScope.qlssoUrl;
    if ($rootScope.qlssoUrl.substr($rootScope.qlssoUrl.length - 1) == "/") {
      ssoEndpoint = ssoEndpoint.substr(0, ssoEndpoint.length - 1);
    }
    console.log("USER ROLES:");
    console.log($rootScope.user.role);

    var qlSecuredListApplUrl = ssoEndpoint + "/admin/open/" + $rootScope.qlTenantId + "/api/list/applications?roles=" + $rootScope.user.role.toString();
    $http({
      method: proxyMethod,
      url: qlSecuredListApplUrl
    }).then(function(response, status, headers, config) {
      $scope.QLSSOApps = [];
      angular.forEach(response.data.applicationDefinitions, function(value, key) {
        if (value.description == "Student Email<br>") {
          value.description = "Student Email"
        }
        $scope.QLSSOApps.push(value)
      });
      $rootScope.isblocking = false;
      $.unblockUI();
    }, function(data, status, headers, config) {
      $.unblockUI();
      console.log("*** Error in opening QL SSO Applets:");
      console.log(data);
      $scope.QLSSOApps = [];
    });

  }

  initApp();


  $scope.openSSOApplet = function(app) {
    var qlUrl = app.launchUrl;
    var exitCallBack = function(data) {
      $.unblockUI();
    };
    var hideBlockUi = function() {
      inAppBrowserRef.show();
      $.unblockUI();
    };

    var username = $.jStorage.get("unifyedusername");
    var password = $.jStorage.get("unifyedpassword");   

    var cd = usernameFieldEl +  ".value = '" + username + "';" + passwordFieldEl + ".value = '" + password + "';" + ssoLoginBtnEl + ".click();";
    var cb = function(data) {};
    var loadStopCallBack = function(data) {
      hideBlockUi();
      inAppBrowserRef.executeScript({
        code: cd
      }, cb);
    };
    $.blockUI();
    var inAppBrowserRef = window.open(qlUrl, "_blank", "location=no,enableViewportScale=yes,hardwareback=no,hidden=yes");

    inAppBrowserRef.addEventListener('loadstop', loadStopCallBack);
    inAppBrowserRef.addEventListener("exit", exitCallBack);
  }

  $scope.confirmDialogSSO = function(app) {

    function callQLUrl(callBack) {
      setTimeout(function() {
        $.ajax({
          type: "POST",
          url: app.resetPromptsUrl,
          xhrFields: {
            withCredentials: true
          },
          crossDomain: true
        }).done(function(data) {
          $.unblockUI();
          if (data) {
            console.log(data);
          }
          return callBack();
        }).error(function() {
          $.unblockUI();
          console.error(data);
          return callBack();
        });
      }, 2000);

    }

    function clearCredential() {
      console.log('clearCredential initiated');
      callQLUrl(function() {
        $.unblockUI();
        console.log('clearCredential completed');
        if ($rootScope.webApp) {
          $.alert({
            animation: 'none',
            icon: 'checkGif',
            title: '',
            content: 'Credentials Cleared',
            columnClass: 'col-lg-6 col-lg-offset-3 col-md-6 col-md-offset-3 col-sm-8 col-sm-offset-2 col-xs-10 col-xs-offset-1',
            buttons: {
              OK: {
                'btnClass': 'btn-primary boxradius popBtns',
                'action': function() {
                  //alert('cancel');
                }
              }
            }
          });

        } else if (window.device) {
          navigator.notification.alert(
            'Credentials Cleared.', // message
            null, // callback
            '', // title
            'OK' // buttonName
          );

        } else {
          alert('Credentials Cleared.');
        }

      });
    }

    if ($rootScope.webApp)
      $.confirm({
        animation: 'none',
        icon: 'fa fa-cog fa-2x',
        title: 'Clear Credentials',
        titleClass: 'proximaSemiBoldFont fontSize18 textcenter',
        content: '',
        columnClass: 'col-lg-6 col-lg-offset-3 col-md-6 col-md-offset-3 col-sm-8 col-sm-offset-2 col-xs-10 col-xs-offset-1',
        buttons: {
          CANCEL: {
            'btnClass': 'btn-cancel boxradius popBtns',
            'action': function() {
              //alert('cancel');
            }
          },
          CLEAR: {
            'btnClass': 'btn-primary boxradius marginleft30px popBtns',
            'action': function() {
              $.blockUI();
              clearCredential();

            }
          }
        }
      });
    else if (window.device) {
      var userdelPost = function(btn) {
        if (btn == 1) {
          $.blockUI();
          clearCredential();
        }
      }

      var options = {
        'androidTheme': window.plugins.actionsheet.ANDROID_THEMES.THEME_HOLO_LIGHT,
        'title': 'Clear Credentials',
        'buttonLabels': ['Clear', 'Cancel'],
        'addCancelButtonWithLabel': 'Cancel'
      };
      window.plugins.actionsheet.show(options, userdelPost);
    }
  }

}

