function pageprocessorEDUPhotosPage3(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);

  $scope.prevArrow = true;
  $scope.nextArrow = true;

  $scope.curindex = $routeParams.index;

  var len = $rootScope.photos.length;
    if ($scope.curindex == 0) {
      $scope.nextArrow = true;
      $scope.prevArrow = false;
    }
    else if($scope.curindex == len-1){
      $scope.nextArrow = false;
      $scope.prevArrow = true;
    }


  $scope.showImage = false;

  $scope.popUpImage = function(index) {
    $scope.index = index;
    $scope.modelImage = $rootScope.photos[index];
    $scope.showImage = true;
  }
  $scope.closeModalPopUp = function() {
    $('.modal').hide(1000);
    $('.modal-backdrop').hide(1000);
  }
  $scope.nextPhoto = function() {
    $scope.modelImage = $rootScope.photos[++$scope.curindex];
    $scope.curindex < len - 1?$scope.prevArrow = true:$scope.nextArrow = false;
  }
  $scope.prevPhoto = function() {
    $scope.modelImage = $rootScope.photos[--$scope.curindex];
    $scope.curindex > 0?$scope.nextArrow = true:$scope.prevArrow = false;
  }
}

