	if($rootScope.webApp){
		
		window.onbeforeunload = function () {


				$rootScope.reloadFTU = true;
		
		};


		
	}

  $scope.nameInitials = function() {
    return $rootScope.user['firstName'].substr(0, 1) + $rootScope.user['lastName'].substr(0, 1);
  };

  var url = '/unifyedcategory/category';

  $scope.callAPI(url, 'GET', {}, function(res) {

    var data = res.data;

    if (data['exception']) {
      if (window.device) {
        navigator.notification.alert(
          'Unable to get preferences.', // message
          null, // callback
          '', // title
          'OK' // buttonName
        );
      } else {
        alert('Unable to get preferences.');
      }
      return;

    }
    $scope.areaOfInterest = data['_embedded']['category'];

    for (var i = 0; i < $scope.areaOfInterest.length; i++) {

      $scope.areaOfInterest[i].selected = ($rootScope.user.areaOfInterest != undefined && $rootScope.user.areaOfInterest.indexOf($scope.areaOfInterest[i]['id']) != -1);
    }

  });

  $scope.preselected = function(index) {
    if (!$scope.areaOfInterest[index].enable)
      return;

    $scope.areaOfInterest[index].selected = !$scope.areaOfInterest[index].selected;
    if ($scope.areaOfInterest[index].selected)
      $scope.selectedCount++;
    else
      $scope.selectedCount--;
  };

  $scope.registerUser = function(data) {
    var url = '/unifydidentity/user';

    $rootScope.callAPI(url, 'POST', data, function(res) {

      $.unblockUI();

      if (!res.data) {
        if (window.device) {
          navigator.notification.alert(
            'Unable to register user.', // message
            null, // callback
            '', // title
            'OK' // buttonName
          );
        } else {
          alert('Unable to register user.');
        }
        return;
      }

      var data = res.data;

      $rootScope.firstTimeUser = false;

      $rootScope.user['id'] = data['id'];
      $rootScope.user['profileBackground'] = data['profileBackground'];
      $rootScope.user['firstName'] = data['firstName'];
      $rootScope.user['lastName'] = data['lastName'];
      $rootScope.user['profileImage'] = data['profileImage'];
      $rootScope.user['role'] = data['role'];
      $rootScope.user['tenant'] = data['tenant'];
      $rootScope.user['email'] = data['email'];
      $rootScope.user['phone'] = data['phone'];
      $rootScope.user['countryCode'] = data['countryCode'];
      $rootScope.user['provider'] = data['provider'];
      $rootScope.user['gender'] = data['gender'];
      $rootScope.user['areaOfInterest'] = data['areaOfInterest'];
      $rootScope.user['channels'] = data['channels'];
      $rootScope.user['devices'] = data['devices'];

      $.jStorage.set('user', $rootScope.user);
      /**
       * Navigate to landing page or home page based on configuration
       */
     /* if (window && window.defaultroute) {
        console.log("Navigate to default route " + window.defaultroute);
        $location.path(window.defaultroute);
      } else*/
	  if ($rootScope.site && $rootScope.site.landingpage) {
        console.log("Navigating to landing page " + $rootScope.site.landingpage);
        $location.path($rootScope.site.landingpage);
      } else {
        // Navigate to landing page set on Preferences app or by default to connect
        if ($rootScope.homeScreen) {
          $location.path($rootScope.homeScreen);
        } else {
          if ($.jStorage.get("homeScreen") == null || $.jStorage.get("homeScreen") == undefined) {
            $.jStorage.set("homeScreen", "whatsup");
            $rootScope.homeScreen = "whatsup";
          } else {
            $rootScope.homeScreen = $.jStorage.get("homeScreen");
          }
          if ($rootScope.homeScreen == "whatsup") {
            $location.path("/app/WhatsUp/WhatsUp");
          } else {
            $location.path("/app/QuickLaunch4/QuickLaunch4");
          }
        }
      }
    });
  }



  $scope.register = function() {
	  


    var pref = [];

    for (var i = 0; i < $scope.areaOfInterest.length; i++) {
      if ($scope.areaOfInterest[i].selected)
        pref.push($scope.areaOfInterest[i].id);
    }

    if (pref.length === 0)
      return;


    $.blockUI();

    $rootScope.user['areaOfInterest'] = pref;


    var url = '/unifydidentity/user/search/findOneByEmail?email=' + $rootScope.user['email'];

    $rootScope.callAPI(url, 'GET', {}, function(res) {

      var data = {};
      if (!res || !res.data) {
        data = {
          firstName: $rootScope.user['firstName'],
          lastName: $rootScope.user['lastName'],
          profileBackground: $rootScope.user['profileBackground'],
          profileImage: $rootScope.user['profileImage'],
          role: $rootScope.user['role'],
          tenant: $rootScope.user['tenant'],
          email: $rootScope.user['email'],
          countryCode: $rootScope.user['countryCode'],
          phone: $rootScope.user['phone'],
          provider: $rootScope.user['provider'],
          gender: $rootScope.user['gender'],
          areaOfInterest: $rootScope.user['areaOfInterest'],
          channels: ['email', 'push']
        };
      } else {
        data = res.data;
        data.firstName = $rootScope.user['firstName'];
        data.lastName = $rootScope.user['lastName'];
        data.profileBackground = $rootScope.user['profileBackground'];
        data.profileImage = $rootScope.user['profileImage'];
        data.role = $rootScope.user['role'];
        data.tenant = $rootScope.user['tenant'];
        data.countryCode = $rootScope.user['countryCode'];
        data.phone = $rootScope.user['phone'];
        data.provider = $rootScope.user['provider'];
        data.gender = $rootScope.user['gender'];
        data.areaOfInterest = $rootScope.user['areaOfInterest'];
        data.channels = ['email', 'push'];

      }

      $.blockUI();
      var pwmsbaseurl = "https://pwms.quicklaunchsso.com";
      if ($rootScope.user.tenantdomain == "unifyed.com") { //AK hardcoding for conference
        pwmsbaseurl = "https://pwms-preprod1.quicklaunchsso.com";
      }
      $http({
        "method": 'POST',
        "url": pwmsbaseurl + '/api/open/validateUserName?tenantDomain=' + $rootScope.user.tenantdomain + '&tenantId=' + $rootScope.user.qlId + '&userName=' + $rootScope.username,
        data: {}
      }).then(function successCallback(res) {
        $.unblockUI();
        if (!res.data.errorMessage) {
          if (!res.data.userVerification.phoneNumber) {
            $http({
              "method": 'POST',
              "url": pwmsbaseurl + '/api/open/getConfiguredEmailId',
              data: res.data.userVerification
            }).then(function successCallback(res) {
              try {
                data.phone = res.data.primaryPhone;
                data.provider = res.data.userVerification.primaryPhoneCarrier;
                console.log('UserDetails', data);
                $scope.registerdevicePush(data);
              } catch (e) {
                console.log(e);
                $scope.registerdevicePush(data);
              }
            }, function errorCallback(err) {
              $.unblockUI();
              $scope.registerdevicePush(data);
            });
          } else {
            data.phone = res.data.userVerification.phoneNumber;
            data.provider = res.data.userVerification.phoneCarrier;
            console.log('UserDetails', data);
            $scope.registerdevicePush(data);
          }
        }
      }, function errorCallback(err) {
        $.unblockUI();
        $scope.registerdevicePush(data);
      });
    });
  }

  $scope.registerdevicePush = function(data) {
    /*  Getting device push IDs when running on mobile device */
    try {
      if (window.device) {
        var push = PushNotification.init({
          android: {
            senderID: "1012473507211"
          },
          browser: {
            pushServiceURL: 'http://push.api.phonegap.com/v1/push'
          },
          ios: {
            alert: "true",
            badge: "true",
            sound: "true"
          },
          windows: {}
        });

        PushNotification.hasPermission(function(permission) {
          if (permission.isEnabled) {
            push.on('registration', function(res) {
              data.devices = [{
                "id": res.registrationId,
                "type": device.platform
              }];
              $scope.registerUser(data);
            });
          } else {
            $scope.registerUser(data);
          }
        });

        $rootScope.inAppNotificationHandler(push);
      } else {
        $scope.registerUser(data);
      }
    } catch (e) {
      alert(e)
    }
  }

  $scope.$apply();
}

